import React, { useState } from "react";

export default function TextForm(props) {
  const handleUpClick = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };
  const handleLowClick = () => {
    let newText = text.toLowerCase();
    setText(newText);
  };
  const handleTitleClick = () => {
    let words = text.split(" ");
    let newText = words
      .map((word) => {
        let newWord = "";
        let cw = word.charAt(0).toUpperCase() + word.slice(1).toLowerCase();
        newWord += cw;
        return newWord;
      })
      .join(" ");
    setText(newText);
  };
  const handleClearClick = () => {
    setText("");
  };
  const handleCopyClick = () => {
    let text = document.getElementById("myBox");
    text.select();
    navigator.clipboard.writeText(text.value);
  };
  const handleOnChange = (event) => {
    console.log("On Change.");
    setText(event.target.value);
  };
  const handleSpeakClick = () => {
    let msg = new SpeechSynthesisUtterance();
    msg.text = text;
    window.speechSynthesis.speak(msg);
  };
  const handleExtraSpaces = () => {
    let newText = text.split(/[ ]+/);
    setText(newText.join(" "));
  };
  const [text, setText] = useState("");
  let textcolor;
  if (props.mode === "dark") {
    textcolor = "text-white";
  }
  return (
    <>
      <div className={`container ${textcolor}`}>
        <h1>{props.heading}</h1>
        <div className="mb-3">
          <textarea
            style={{
              backgroundColor: props.mode === "dark" ? "191919" : "white",color:{textcolor}
            }}
            placeholder="Enter text here"
            value={text}
            className="form-control"
            id="myBox"
            rows="8"
            onChange={handleOnChange}
          ></textarea>
        </div>
        <button className="btn btn-primary mx-1" onClick={handleUpClick}>
          Convert to Uppercase
        </button>
        <button className="btn btn-primary mx-1" onClick={handleLowClick}>
          Convert to Lowercase
        </button>
        <button className="btn btn-primary mx-1" onClick={handleTitleClick}>
          Convert to Titlecase
        </button>
        <button className="btn btn-primary mx-1" onClick={handleClearClick}>
          Clear Text
        </button>
        <button className="btn btn-primary mx-1" onClick={handleCopyClick}>
          Copy Text
        </button>
        <button className="btn btn-primary mx-1" onClick={handleExtraSpaces}>
          Remove Extra Spaces
        </button>
        <button className="btn btn-primary mx-1" onClick={handleSpeakClick}>
          Read the text
        </button>
      </div>
      <div className={`container ${textcolor} my-3`}>
        <h2>YOUR TEXT SUMMARY</h2>
        <p>
          {text.split(" ").length} words and {text.length} characters
        </p>
        <p>{0.0008 * text.split(" ").length} Minutes read</p>
        <h2>TEXT PREVIEW</h2>
        <p>{text}</p>
      </div>
    </>
  );
}
