import "./App.css";
import React, { useState } from "react";
import About from "./components/About";
import Navbar from "./components/Navbar";
import TextForm from "./components/TextForm";
import Alert from "./components/Alert";
function App() {
  const [mode, setMode] = useState("light");
  const[sitecolor,setSiteColor] = useState("white")
  const [alert, setAlert] = useState(null);
  const toggleSiteColorw = () =>{
    setSiteColor("white");
    document.body.style.backgroundColor = "#F2F2F2";
  }
  const toggleSiteColorr = () =>{
    setSiteColor("red");
    document.body.style.backgroundColor = "red";
  }
  const toggleSiteColorb = () =>{
    setSiteColor("blue");
    document.body.style.backgroundColor = "#2563EB";
  }
  const toggleSiteColorg = () =>{
    setSiteColor("green");
    document.body.style.backgroundColor = "#A6FF00";
  }
  const valueOfAlert = (message, type) => {
    setAlert({
      msg: message,
      type: type,
    });
    setTimeout(() => {
      setAlert(null);
    }, 2000);
  };
  const toggleMode = () => {
    if (mode === "light") {
      setMode("dark");
      document.body.style.backgroundColor = "#2F2F2F";
      valueOfAlert("DARK MODE has been enabled.", "success");
    } else {
      setMode("light");
      document.body.style.backgroundColor = "#F2F2F2";
      valueOfAlert("LIGHT MODE has been enabled.", "success");
    }
  };
  return (
    <>
      <Navbar
        title="TextEdit"
        about="About Us"
        sitecolor={sitecolor}
        toggleSiteColorw ={toggleSiteColorw}
        toggleSiteColorr ={toggleSiteColorr}
        toggleSiteColorb ={toggleSiteColorb}
        toggleSiteColorg ={toggleSiteColorg}
        mode={mode}
        toggleMode={toggleMode}
      />
      <Alert alert={alert} />
      <div className="container my-3">
        <About mode={mode} />
        <TextForm
          showAlert={valueOfAlert}
          heading="Enter the text to analyze below"
          mode={mode}
        />
      </div>
    </>
  );
}

export default App;
